import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import styled from 'styled-components';
import Home from './pages/Home';
import Login from './pages/Login';
import SignUp from './pages/SignUp';
import MyReviews from './pages/MyReviews';
import MyInfo from './pages/MyInfo';
import logo from './assets/LOGO.png';
import homeIcon from './assets/home_icon.svg';
import reviewIcon from './assets/review_icon.svg';
import profileIcon from './assets/profile_icon.svg';

const App = () => {
  return (
    <Router>
      <Container>
        <Header>
          <TopBar>
            <StyledLink to="/signup">회원가입</StyledLink>
            <StyledLink to="/login">로그인</StyledLink>
          </TopBar>
        </Header>

        <Main>
          <LogoContainer>
            <Logo src={logo} alt="Wine Tale Logo" />
            <Title>Wine Tale</Title>
          </LogoContainer>

          <SearchBar>
            <SearchInput type="text" placeholder="와인을 검색하세요..." />
            <SearchIcon>🔍</SearchIcon>
          </SearchBar>
        </Main>

        <Footer>
          <NavBar>
            <NavItem>
              <NavLink to="/">
                <NavIcon src={homeIcon} alt="홈 아이콘" />
                <NavText>홈</NavText>
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink to="/reviews">
                <NavIcon src={reviewIcon} alt="리뷰 아이콘" />
                <NavText>내 리뷰</NavText>
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink to="/info">
                <NavIcon src={profileIcon} alt="프로필 아이콘" />
                <NavText>내 정보</NavText>
              </NavLink>
            </NavItem>
          </NavBar>
        </Footer>

        {/* 라우터 설정 */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<SignUp />} />
          <Route path="/reviews" element={<MyReviews />} />
          <Route path="/info" element={<MyInfo />} />
        </Routes>
      </Container>
    </Router>
  );
};

export default App;

// Styled Components
const Container = styled.div`
  font-family: Arial, sans-serif;
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 600px;
  margin: 0 auto;
  min-height: 100vh;
  position: relative;
  padding-bottom: 60px; /* Footer 공간 확보 */
`;

const Header = styled.header`
  width: 100%;
  position: absolute;
  top: 0;
`;

const TopBar = styled.div`
  display: flex;
  justify-content: flex-end;
  padding: 10px 20px; /* 오른쪽에 추가 여백 확보 */
  width: 100%;
  box-sizing: border-box;
`;

const StyledLink = styled(Link)`
  margin-left: 10px;
  text-decoration: none;
  color: black;
  font-size: 16px;
`;


const Main = styled.main`
  text-align: center;
  margin-top: 100px; /* Adjusted to avoid overlap with TopBar */
`;

const LogoContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Logo = styled.img`
  width: 100px;

  @media (max-width: 768px) {
    width: 80px;
  }

  @media (max-width: 480px) {
    width: 60px;
  }
`;

const Title = styled.h1`
  font-size: 24px;
  color: #333;
  margin-top: 10px;

  @media (max-width: 768px) {
    font-size: 20px;
  }

  @media (max-width: 480px) {
    font-size: 18px;
  }
`;

const SearchBar = styled.div`
  position: relative;
  width: 80%;
  max-width: 500px;
  margin: 20px;

  @media (max-width: 768px) {
    width: 90%;
  }
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 10px 20px;
  border-radius: 25px;
  border: 1px solid #ccc;
  font-size: 16px;

  @media (max-width: 480px) {
    padding: 8px 15px;
    font-size: 14px;
  }
`;

const SearchIcon = styled.span`
  position: absolute;
  right: 15px;
  top: 50%;
  transform: translateY(-50%);
  font-size: 20px;
  color: #888;

  @media (max-width: 480px) {
    font-size: 18px;
  }
`;

const Footer = styled.footer`
  position: fixed;
  bottom: 0;
  left: 0;
  width: 100vw;
  background-color: #f9f9f9;
  border-top: 1px solid #ccc;
  padding: 10px 0;
  z-index: 1000;
`;

const NavBar = styled.div`
  display: flex;
  justify-content: space-around; /* 아이템을 균등하게 배치 */
  width: 100%;
  max-width: 600px;
  margin: 0 auto;
`;

const NavItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  color: #888;
  flex: 1; /* 아이템이 균등하게 공간을 차지 */
`;

const NavIcon = styled.img`
  width: 24px;
  height: 24px;
  margin-bottom: 5px;

  @media (max-width: 768px) {
    width: 20px;
    height: 20px;
  }

  @media (max-width: 480px) {
    width: 18px;
    height: 18px;
  }
`;

const NavText = styled.span`
  font-size: 14px;

  @media (max-width: 768px) {
    font-size: 12px;
  }
`;
