INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Merlot', '메를로는 부드럽고 둥근 질감으로 유명하며, 블랙베리와 초콜릿 노트를 가진 레드 와인을 만듭니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Roussanne', '루산느는 허브와 꿀 향이 특징인 화이트 와인 품종으로, 주로 프랑스 남부에서 생산됩니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Fiano', '피아노는 꿀과 열대 과일 향을 가진 이탈리아 화이트 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Tempranillo', '템프라니요는 스페인의 대표적인 레드 와인 품종으로, 체리와 담배 향이 조화를 이룹니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Touriga Nacional', '투리가 내셔널은 포르투갈의 대표 품종으로, 강렬한 향과 타닌이 특징입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Grüner Veltliner', '그뤼너 벨트리너는 오스트리아의 대표 품종으로, 신선한 허브와 감귤 풍미가 특징입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Cabernet Franc', '카베르네 프랑은 블렌드와 단독 와인에 모두 사용되며, 허브와 붉은 과일 풍미가 특징입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Sémillon', '세미용은 꿀과 건과일 향이 특징이며, 달콤한 와인과 드라이 와인에 모두 사용됩니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Sangiovese', '산지오베제는 이탈리아의 대표 품종으로, 체리와 허브 풍미가 돋보이는 레드 와인을 만듭니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Barbera', '바르베라는 높은 산미와 밝은 체리 향으로 유명한 이탈리아 레드 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Lambrusco', '람브루스코는 붉은 과일 풍미와 스파클링 스타일로 유명한 이탈리아 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Pinot Grigio', '피노 그리지오는 가벼운 화이트 와인으로, 배와 사과 풍미가 특징입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Chardonnay', '샤르도네는 다양한 스타일의 화이트 와인으로 생산되며, 사과와 바닐라 향을 가진 것으로 유명합니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Pedro Ximénez', '페드로 히메네스는 매우 달콤한 스페인 와인 품종으로, 디저트 와인에 주로 사용됩니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Verdelho', '베르델로는 상쾌한 산미와 열대 과일 향이 돋보이는 화이트 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Syrah', '시라(또는 쉬라즈)는 강렬한 블랙 페퍼와 다크 초콜릿 향을 가진 풀바디 레드 와인으로 유명합니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Cabernet Sauvignon', '카베르네 소비뇽은 풀바디 레드 와인으로 잘 알려져 있으며, 블랙커런트와 스파이스의 풍미가 특징입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Grenache', '그르나슈는 붉은 과일과 허브 향이 특징이며, 종종 블렌드 와인의 주요 품종으로 사용됩니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Tannat', '타나는 강렬한 타닌과 다크 초콜릿 풍미가 돋보이는 레드 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Torrontés', '토론테스는 아르헨티나의 독특한 화이트 와인 품종으로, 꽃 향과 열대 과일 풍미가 돋보입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Petit Verdot', '프티 베르도는 블랙커런트와 제비꽃 향이 돋보이며, 종종 블렌드에 사용되는 레드 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Nebbiolo', '네비올로는 타닌이 강하고 체리와 장미 향이 특징인 이탈리아 레드 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Moscato', '모스카토는 달콤한 맛과 꽃 향이 특징이며, 종종 디저트 와인으로 사용됩니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Riesling', '리슬링은 높은 산미와 복합적인 향으로 유명하며, 특히 달콤한 화이트 와인으로 많이 생산됩니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Mourvèdre', '무르베드르는 스파이시하고 다크한 풍미를 가진 레드 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Trebbiano', '트레비아노는 신선한 산미와 부드러운 풍미를 가진 이탈리아 화이트 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Cinsault', '생소는 밝은 과일 향과 부드러운 질감이 특징인 레드 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Pinot Noir', '피노 누아는 섬세하고 복합적인 향으로 사랑받으며, 체리와 라즈베리 풍미가 돋보이는 레드 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Corvina', '코르비나는 이탈리아 발폴리첼라 와인의 주요 품종으로, 체리와 아몬드 향이 특징입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Sauvignon Blanc', '소비뇽 블랑은 신선한 허브와 감귤 향이 돋보이는 화이트 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Malbec', '말벡은 풍부한 블랙베리와 자두 향이 돋보이는 풀바디 레드 와인으로 잘 알려져 있습니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Aglianico', '알리아니코는 이탈리아의 남부에서 주로 생산되며, 다크 체리와 초콜릿 풍미가 돋보입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Gamay', '가메는 밝고 신선한 레드 와인으로, 라즈베리와 체리 향을 가지고 있습니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Gewürztraminer', '게뷔르츠트라미너는 독특한 향신료와 장미 향이 특징인 화이트 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Aligoté', '알리고테는 상쾌한 산미와 레몬 향이 특징인 화이트 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Carmenère', '까르메네르는 칠레의 대표 품종으로, 블랙커런트와 초콜릿 향이 조화를 이룹니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Viognier', '비오니에는 복숭아와 아카시아 꽃 향을 가진 화이트 와인 품종입니다.');
INSERT IGNORE INTO Grape (grape_name, grape_ex) VALUES ('Arneis', '아르네이스는 신선한 배와 꽃 향을 가진 이탈리아 화이트 와인 품종입니다.');
